mapper.select:
SELECT COMNO, COMNAME, COMTYPE, COMADDR, COMPRES, COMPIC, COMMAP
FROM company 
WHERE COMNO = #{value}

mapper.update:
UPDATE company
COMNAME = #{companyName:VARCHAR}, COMTYPE = #{companyType:VARCHAR}, COMADDR = #{companyAddr:VARCHAR}, COMPRES = #{companyPresentation:VARCHAR}, COMPIC = #{companyPicture:VARCHAR}, COMMAP = #{companyMap:VARCHAR}
WHERE COMNO = #{companyNumber:VARCHAR}
